import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';

declare global {
  interface Window {
    Blotter: any;
  }
}

interface LoadingTextProps {
  $size: number;
  $fill: string;
}

const TextContainer = styled.div`
  canvas {
    width: 100% !important;
    height: auto !important;
  }
`;

const LoadingText = styled.div<LoadingTextProps>`
  font-size: ${props => props.$size}px;
  color: ${props => props.$fill};
  text-align: center;
  font-family: 'Creepster', cursive;
`;

interface BlotterTextProps {
  text: string;
  size?: number;
  fill?: string;
  className?: string;
}

const MathUtils = {
  lineEq: (y2: number, y1: number, x2: number, x1: number, currentVal: number) => {
    const m = (y2 - y1) / (x2 - x1);
    const b = y1 - m * x1;
    return m * currentVal + b;
  },
  lerp: (a: number, b: number, n: number) => (1 - n) * a + n * b,
  distance: (x1: number, x2: number, y1: number, y2: number) => {
    const a = x1 - x2;
    const b = y1 - y2;
    return Math.hypot(a, b);
  }
};

export const BlotterText: React.FC<BlotterTextProps> = ({ 
  text, 
  size = 100,
  fill = '#fff',
  className 
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const materialRef = useRef<any>(null);
  const blotterRef = useRef<any>(null);
  const [isLoading, setIsLoading] = useState(true);
  const lastMousePosition = useRef({ x: 0, y: 0 });
  const volatilityRef = useRef(0);
  const animationFrameRef = useRef<number>();

  useEffect(() => {
    const checkBlotter = () => {
      if (window.Blotter) {
        setIsLoading(false);
        initBlotter();
      } else {
        setTimeout(checkBlotter, 100);
      }
    };

    const render = () => {
      if (!materialRef.current) return;

      const mousePos = { x: 0, y: 0 };
      const docEl = document.documentElement;
      const body = document.body;

      const docScrolls = {
        left: body.scrollLeft + docEl.scrollLeft,
        top: body.scrollTop + docEl.scrollTop
      };

      // Get relative mouse position
      const relmousepos = {
        x: mousePos.x - docScrolls.left,
        y: mousePos.y - docScrolls.top
      };

      // Calculate mouse movement distance
      const mouseDistance = MathUtils.distance(
        lastMousePosition.current.x,
        relmousepos.x,
        lastMousePosition.current.y,
        relmousepos.y
      );

      // Update volatility based on mouse movement and scroll
      volatilityRef.current = MathUtils.lerp(
        volatilityRef.current,
        Math.min(MathUtils.lineEq(0.9, 0, 100, 0, mouseDistance), 0.9),
        0.05
      );

      // Apply the effect
      materialRef.current.uniforms.uVolatility.value = volatilityRef.current;

      // Store last mouse position
      lastMousePosition.current = { x: relmousepos.x, y: relmousepos.y };

      // Continue animation loop
      animationFrameRef.current = requestAnimationFrame(render);
    };

    const handleMouseMove = (e: MouseEvent) => {
      const mousePos = {
        x: e.clientX,
        y: e.clientY
      };

      if (materialRef.current) {
        const distanceFromCenter = Math.sqrt(
          Math.pow(mousePos.x - window.innerWidth / 2, 2) +
          Math.pow(mousePos.y - window.innerHeight / 2, 2)
        );

        const maxDistance = Math.sqrt(
          Math.pow(window.innerWidth / 2, 2) +
          Math.pow(window.innerHeight / 2, 2)
        );

        const mouseInfluence = distanceFromCenter / maxDistance;
        materialRef.current.uniforms.uVolatility.value = mouseInfluence * 1.2 + 0.15;
      }
    };

    const initBlotter = () => {
      try {
        if (!window.Blotter || !window.Blotter.LiquidDistortMaterial) {
          console.error('Blotter or LiquidDistortMaterial not loaded');
          return;
        }

        const material = new window.Blotter.LiquidDistortMaterial();
        materialRef.current = material;

        // Increase initial distortion values
        material.uniforms.uSpeed.value = 1.0;
        material.uniforms.uVolatility.value = 0.15;
        material.uniforms.uSeed.value = 0.1;

        const blotter = new window.Blotter(material, {
          texts: [
            new window.Blotter.Text(text, {
              family: "Creepster",
              size: size,
              fill: fill,
              paddingLeft: 100,
              paddingRight: 100,
              weight: 400,
              leading: 1,
              tracking: 0,
            }),
          ],
        });

        blotterRef.current = blotter;

        const scope = blotter.forText(blotter.texts[0]);
        if (containerRef.current) {
          while (containerRef.current.firstChild) {
            containerRef.current.removeChild(containerRef.current.firstChild);
          }
          containerRef.current.appendChild(scope.domElement);
        }

        // Start animation loop with enhanced parameters
        let lastTime = 0;
        const animate = (currentTime: number) => {
          if (!materialRef.current) return;

          const deltaTime = currentTime - lastTime;
          lastTime = currentTime;

          // Continuously animate base distortion
          material.uniforms.uSeed.value = (material.uniforms.uSeed.value + deltaTime * 0.0005) % 1;

          // Add subtle continuous movement
          const time = currentTime * 0.001;
          const baseVolatility = Math.sin(time) * 0.1 + 0.1;
          material.uniforms.uVolatility.value = Math.max(material.uniforms.uVolatility.value, baseVolatility);

          animationFrameRef.current = requestAnimationFrame(animate);
        };

        animationFrameRef.current = requestAnimationFrame(animate);

        // Add event listeners with enhanced sensitivity
        window.addEventListener('mousemove', handleMouseMove);
        window.addEventListener('scroll', () => {
          if (materialRef.current) {
            const scrolled = window.pageYOffset;
            const maxScroll = document.documentElement.scrollHeight - window.innerHeight;
            const scrollProgress = scrolled / maxScroll;
            // Increase scroll-based distortion
            material.uniforms.uVolatility.value = scrollProgress * 0.8 + 0.1;
          }
        });
      } catch (error) {
        console.error('Error initializing Blotter:', error);
        setIsLoading(true); // Keep showing loading state if there's an error
      }
    };

    checkBlotter();

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      window.removeEventListener('mousemove', handleMouseMove);
      if (blotterRef.current) {
        blotterRef.current.destroy();
      }
    };
  }, [text, size, fill]);

  if (isLoading) {
    return <LoadingText $size={size} $fill={fill}>{text}</LoadingText>;
  }

  return <TextContainer ref={containerRef} className={className} />;
};
