import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import * as THREE from 'three';

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: -1;
  canvas {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
  }
`;

const portalVertexShader = `
  varying vec2 vUv;
  varying vec3 vPosition;

  void main() {
    vUv = uv;
    vPosition = position;
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
  }
`;

const portalFragmentShader = `
  uniform float time;
  varying vec2 vUv;
  varying vec3 vPosition;

  // Noise functions
  vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
  vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
  vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

  float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i);
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
      + i.x + vec3(0.0, i1.x, 1.0 ));
    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
  }

  void main() {
    // Create multiple layers of swirling void
    vec2 center = vec2(0.5, 0.5);
    vec2 p = vUv - center;
    float r = length(p);
    float angle = atan(p.y, p.x);

    // Create multiple rotating layers
    float swirl1 = angle - time * 0.1;  // Slow base rotation
    float swirl2 = angle + time * 0.15; // Counter-rotating layer
    float swirl3 = angle - time * 0.05; // Slower deep layer

    // Create depth layers with different frequencies
    float depth1 = sin(15.0 * (swirl1 + r * 2.0)) * 0.5 + 0.5;
    float depth2 = sin(10.0 * (swirl2 + r * 3.0)) * 0.5 + 0.5;
    float depth3 = sin(5.0 * (swirl3 + r * 4.0)) * 0.5 + 0.5;

    // Dynamic noise for energy effect
    float noise1 = snoise(vec2(vUv.x * 3.0 + time * 0.5, vUv.y * 3.0 + time * 0.5));
    float noise2 = snoise(vec2(vUv.x * 5.0 - time, vUv.y * 5.0));
    float noise3 = snoise(vec2(vUv.x * 2.0 + time * 0.2, vUv.y * 2.0));

    // Rich color palette for depth
    vec3 deepVoid = vec3(0.02, 0.0, 0.05);  // Almost black with slight purple
    vec3 color1 = vec3(0.4, 0.0, 0.8);      // Deep purple
    vec3 color2 = vec3(0.0, 0.6, 1.0);      // Bright blue
    vec3 color3 = vec3(0.8, 0.3, 1.0);      // Vibrant purple

    // Layer the colors with depth
    vec3 finalColor = deepVoid;

    // Add deep swirling layers
    finalColor = mix(finalColor, color1, depth1 * (1.0 - r) * 0.7);
    finalColor = mix(finalColor, color2, depth2 * (1.0 - r) * 0.5);
    finalColor = mix(finalColor, color3, depth3 * (1.0 - r) * 0.3);

    // Add noise-based energy fluctuations
    finalColor += color2 * noise1 * 0.15 * (1.0 - r);
    finalColor += color3 * noise2 * 0.1 * (1.0 - r);

    // Create void effect
    float voidFactor = smoothstep(0.0, 1.5, r);
    finalColor = mix(finalColor, deepVoid, voidFactor);

    // Add subtle energy streams
    float energyStreams = sin(noise3 * 10.0 + r * 20.0 - time) * 0.5 + 0.5;
    finalColor += color2 * energyStreams * 0.1 * (1.0 - voidFactor);

    // Add subtle glitch effect
    float glitch = step(0.99, sin(time * 5.0 + noise2 * 10.0));
    finalColor = mix(finalColor, color2, glitch * 0.05);

