import React, { useEffect, useState, useRef } from 'react';
import styled from 'styled-components';
import { BlotterText } from './BlotterText';

const HeroContainer = styled.section`
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
`;

const BlotterContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const useViewportSize = () => {
  const [size, setSize] = useState({ width: 0, height: 0 });

  useEffect(() => {
    const updateSize = () => {
      setSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', updateSize);
    updateSize();

    return () => window.removeEventListener('resize', updateSize);
  }, []);

  return size;
};

export const Hero: React.FC = () => {
  const { width, height } = useViewportSize();
  const containerRef = useRef<HTMLDivElement>(null);
  const [fontSize, setFontSize] = useState(100);
  const text = "SQUANCH";

  useEffect(() => {
    const calculateIdealFontSize = () => {
      if (!containerRef.current) return;

      // Get container dimensions with safety margins
      const containerWidth = width * 0.9; // 90% of viewport width
      const containerHeight = height * 0.9; // 90% of viewport height

      // Approximate the aspect ratio of the text (width to height ratio)
      // For Creepster font, we'll use a conservative estimate
      const textAspectRatio = 0.6 * text.length; // width is roughly 0.6 times height per character

      // Calculate font size based on width
      const fontSizeByWidth = containerWidth / textAspectRatio;
      // Calculate font size based on height
      const fontSizeByHeight = containerHeight;

      // Use the smaller of the two sizes to ensure text fits both dimensions
      const newFontSize = Math.min(fontSizeByWidth, fontSizeByHeight);

      setFontSize(newFontSize);
    };

    calculateIdealFontSize();
  }, [width, height, text]);

  return (
    <HeroContainer>
      <BlotterContainer ref={containerRef}>
        <BlotterText 
          text={text}
          size={fontSize}
          fill="#ffffff"
        />
      </BlotterContainer>
    </HeroContainer>
  );
};
