 * The $isHeader prop controls whether the text takes full viewport height.
 * DO NOT modify the width/height calculations or flex properties.
 */
const TextContainer = styled.div<{ $isHeader?: boolean }>`
  width: 100%;
  height: ${props => props.$isHeader ? '100vh' : 'auto'};
  display: flex;
  align-items: center;
  justify-content: center;
  canvas {
    width: 100% !important;
    height: ${props => props.$isHeader ? '100vh' : 'auto'} !important;
    object-fit: contain;
  }
`;

/**
 * Fallback component when WebGL is not available
 * Maintains consistent styling with the WebGL version
 */
const FallbackText = styled.div<{ $isHeader?: boolean }>`
  width: 100%;
  height: ${props => props.$isHeader ? '100vh' : 'auto'};
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'Creepster', cursive;
  font-size: ${props => props.$isHeader ? '4rem' : '2rem'};
  color: ${props => props.color || '#fff'};
`;

interface BlotterTextProps {
  text: string;
  size?: number;
  fill?: string;
  className?: string;
  isHeader?: boolean;
}

/**
 * ⚠️ CRITICAL SHADER CODE
 * Basic vertex shader that passes UV coordinates to fragment shader
 * DO NOT modify unless you fully understand WebGL shaders
 */
const vertexShader = `
  varying vec2 vUv;
  void main() {
    vUv = uv;
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
  }
`;

/**
 * ⚠️ CRITICAL SHADER CODE
 * Fragment shader that creates the liquid distortion effect
 * Carefully tuned parameters for stability and performance
 * DO NOT modify the following:
 * - UV clamping
 * - Alpha channel handling
 * - Distortion parameters
 */
const fragmentShader = `
  uniform float time;
  uniform float distortionFactor;
  uniform float scrollVelocity;
  uniform vec2 mousePos;
  uniform vec3 color;
  uniform sampler2D textTexture;
  varying vec2 vUv;

  void main() {
    vec2 uv = vUv;

    // Calculate distance from mouse with much smaller radius but sharper falloff
    float dist = distance(mousePos, uv);
    float influence = smoothstep(0.1, 0.0, dist) * distortionFactor;
    influence = pow(influence, 0.7); // Sharper falloff but maintain strength

    // Add scroll-based stretching effect with limited magnitude (max 10% stretch)
    float scrollStretch = scrollVelocity * 0.05 * (1.0 - uv.y);
    scrollStretch *= 1.0 + sin(uv.y * 3.14159 * 2.0) * 0.5;
    scrollStretch = clamp(scrollStretch, -0.1, 0.1);
    uv.y += scrollStretch;

    // Add minimal horizontal wave distortion during scroll
    float scrollWave = sin(uv.y * 20.0 + time * 2.0) * scrollVelocity * 0.005;
    uv.x += scrollWave;

    // Create more intense wave effect with higher frequency (mouse interaction)
    float wave = sin(uv.x * 30.0 + time) * 0.06 * influence;
    wave += cos(uv.y * 25.0 + time * 0.8) * 0.04 * influence;
    uv.y += wave;

    // Create more detailed liquid-like distortion
    float distortion = sin(uv.x * 20.0 + time * 1.5) * cos(uv.y * 15.0 + time) * influence;
    distortion += sin(uv.y * 12.0 - time) * 0.02 * influence;
    uv += distortion * 0.05;

    // Add a tighter ripple effect
    float ripple = sin(dist * 50.0 - time * 3.0) * 0.03 * influence;
    uv += ripple;

    // Sample the text texture with clamping
    vec2 clampedUv = clamp(uv, 0.0, 1.0);
    vec4 texColor = texture2D(textTexture, clampedUv);

    // Use the red channel as alpha (since we drew white text on black)
    float alpha = texColor.r;

    // Output final color
    gl_FragColor = vec4(color, alpha);
  }
`;

/**
 * ⚠️ CRITICAL INITIALIZATION
 * Static WebGL availability check
 * DO NOT modify this check as it's essential for fallback handling
 */
const webGLAvailable = (() => {
  try {
    const canvas = document.createElement('canvas');
    return !!(window.WebGLRenderingContext && 
      (canvas.getContext('webgl') || canvas.getContext('experimental-webgl')));
  } catch (e) {
    return false;
  }
})();

/**
 * ⚠️ CRITICAL UTILITY
 * Color parsing utility that handles rgba values
 * Essential for proper color handling in WebGL
 */
const parseRGBA = (color: string): string => {
  if (color.startsWith('rgba')) {
    const matches = color.match(/rgba?\((\d+),\s*(\d+),\s*(\d+)/);
    if (matches) {
      return `rgb(${matches[1]}, ${matches[2]}, ${matches[3]})`;
    }
  }
  return color;
};

/**
 * Simple font loading check using document.fonts API
 */
const checkFontLoaded = async (fontFamily: string): Promise<boolean> => {
  if ('fonts' in document) {
    try {
      await document.fonts.load(`1em ${fontFamily}`);
      return document.fonts.check(`1em ${fontFamily}`);
    } catch (e) {
      console.warn('Font loading check failed:', e);
      return true; // Continue anyway
    }
  }
  return true; // If document.fonts is not supported, assume font is loaded
};

/**
 * Utility function for smooth interpolation
 */
const lerp = (start: number, end: number, factor: number) => {
  return start + (end - start) * factor;
};

