      // Use power-of-two dimensions for textures, ensuring minimum size
      const minSize = 256;
      const textureWidth = Math.max(minSize, Math.pow(2, Math.ceil(Math.log2(Math.min(2048, initialWidth)))));
      const textureHeight = Math.max(minSize, Math.pow(2, Math.ceil(Math.log2(textureWidth / containerAspect))));

      canvas.width = textureWidth;
      canvas.height = textureHeight;

      // Fill canvas with black background
      ctx.fillStyle = '#000000';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Calculate and set font size
      const fontSize = isHeader 
        ? Math.floor(canvas.height * 0.25)
        : Math.floor(canvas.height * 0.8);

      ctx.font = `bold ${fontSize}px Creepster`;
      ctx.fillStyle = '#ffffff';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';

      // Measure and scale text if needed
      const textMetrics = ctx.measureText(text);
      const textWidth = textMetrics.width;
      const maxWidth = canvas.width * 0.9;

      if (textWidth > maxWidth) {
        const scale = maxWidth / textWidth;
        ctx.font = `bold ${Math.floor(fontSize * scale)}px Creepster`;
      }

      // Draw text
      ctx.fillText(text, canvas.width / 2, canvas.height / 2);

      // Create texture with proper settings
      const texture = new THREE.Texture(canvas);
      texture.needsUpdate = true;
      texture.minFilter = THREE.LinearFilter;
      texture.magFilter = THREE.LinearFilter;
      texture.format = THREE.RGBAFormat;
      texture.type = THREE.UnsignedByteType;

      // Parse color properly
      const color = new THREE.Color();
      try {
        const parsedColor = parseRGBA(fill);
        color.setStyle(parsedColor);
      } catch (e) {
        console.warn('Color parsing failed, using default white');
        color.setRGB(1, 1, 1);
      }

      // Create shader material
      const material = new THREE.ShaderMaterial({
        uniforms: {
          time: { value: 0 },
          distortionFactor: { value: 0 },
          scrollVelocity: { value: 0 },
          mousePos: { value: new THREE.Vector2(0.5, 0.5) },
          color: { value: color },
          textTexture: { value: texture }
        },
        vertexShader: vertexShader,
        fragmentShader: fragmentShader,
        transparent: true,
        depthWrite: false,
        depthTest: false
      });
      materialRef.current = material;

      // Create mesh with proper aspect ratio
      const geometry = new THREE.PlaneGeometry(2, 2 / containerAspect);
      const mesh = new THREE.Mesh(geometry, material);
      scene.add(mesh);

      let isAnimating = true;
      let lastTime = performance.now();
      let deltaTime = 0;

      const animate = () => {
        if (!isAnimating || !material || !renderer || !scene || !camera) return;

        const currentTime = performance.now();
        deltaTime = (currentTime - lastTime) / 1000; // Convert to seconds
        lastTime = currentTime;

        // Smooth out scroll velocity
        scrollVelocity.current = lerp(
          scrollVelocity.current,
          targetScrollVelocity.current,
          Math.min(1, deltaTime * 8) // Adjust this value to control smoothness
        );

        material.uniforms.time.value += deltaTime;
        material.uniforms.mousePos.value.set(mousePos.current.x, mousePos.current.y);
        material.uniforms.scrollVelocity.value = scrollVelocity.current;

        // Natural velocity decay
        targetScrollVelocity.current *= Math.pow(0.95, deltaTime * 60);

        renderer.render(scene, camera);
        frameRef.current = requestAnimationFrame(animate);
      };

      frameRef.current = requestAnimationFrame(animate);

      // Handle mouse movement
      const handleMouseMove = (e: MouseEvent) => {
        const rect = containerRef.current?.getBoundingClientRect();
        if (!rect) return;

        mousePos.current = {
          x: (e.clientX - rect.left) / rect.width,
          y: 1 - (e.clientY - rect.top) / rect.height
        };

        if (materialRef.current) {
          const dist = Math.hypot(
            mousePos.current.x - 0.5,
            mousePos.current.y - 0.5
          );
          // Increase maximum distortion for more intense effect
          materialRef.current.uniforms.distortionFactor.value = Math.min(dist * 4, 2.0);
        }
      };

      window.addEventListener('mousemove', handleMouseMove);

      // Handle resize
      const handleResize = () => {
        if (!containerRef.current || !renderer || !camera) return;

        const newWidth = containerRef.current.clientWidth;
        const newHeight = isHeader ? window.innerHeight : containerRef.current.clientHeight;
        const newAspect = newWidth / newHeight;

        renderer.setSize(newWidth, newHeight);

        camera.left = -1;
        camera.right = 1;
        camera.top = 1 / newAspect;
        camera.bottom = -1 / newAspect;
        camera.updateProjectionMatrix();
      };

      window.addEventListener('resize', handleResize);
      handleResize();

      // Improved scroll handler with better velocity calculation
      let lastScrollTime = performance.now();
      let lastScrollDelta = 0;

      const handleScroll = () => {
        const currentScrollY = window.scrollY;
        const currentTime = performance.now();
        const timeDelta = (currentTime - lastScrollTime) / 1000; // Convert to seconds
        const scrollDelta = currentScrollY - lastScrollY.current;

        if (timeDelta > 0) {
          // Calculate instantaneous velocity with reduced sensitivity
          const instantVelocity = scrollDelta / timeDelta / window.innerHeight;

          // Smooth out sudden changes in velocity
          const smoothedVelocity = lerp(lastScrollDelta / timeDelta / window.innerHeight, instantVelocity, 0.3); // Reduced from 0.5

          // Update target velocity with reduced clamping
          targetScrollVelocity.current = Math.max(-1.5, Math.min(1.5, smoothedVelocity)); // Reduced from ±4 to ±1.5

          lastScrollDelta = scrollDelta;
          lastScrollTime = currentTime;
        }

        lastScrollY.current = currentScrollY;
      };

      // Add scroll listener
      window.addEventListener('scroll', handleScroll, { passive: true });

      // Handle WebGL context loss
      const handleContextLost = () => {
        isAnimating = false;
        setHasError(true);
      };

      const handleContextRestored = () => {
        isAnimating = true;
        setHasError(false);
      };

      renderer.domElement.addEventListener('webglcontextlost', handleContextLost);
      renderer.domElement.addEventListener('webglcontextrestored', handleContextRestored);

      return () => {
        isAnimating = false;
        if (frameRef.current) {
          cancelAnimationFrame(frameRef.current);
        }
        window.removeEventListener('mousemove', handleMouseMove);
        window.removeEventListener('resize', handleResize);
        window.removeEventListener('scroll', handleScroll);
        renderer.domElement.removeEventListener('webglcontextlost', handleContextLost);
        renderer.domElement.removeEventListener('webglcontextrestored', handleContextRestored);
        renderer.dispose();
        geometry.dispose();
        material.dispose();
        texture.dispose();
      };
    } catch (e) {
      console.error('Setup error:', e);
      setHasError(true);
      return;
    }
  }, [text, size, fill, isHeader, hasError, fontLoaded]);

  if (!fontLoaded || hasError) {
    return (
      <FallbackText 
        $isHeader={isHeader} 
        className={className}
        color={fill}
      >
        {text}
      </FallbackText>
    );
  }

  return <TextContainer ref={containerRef} className={className} $isHeader={isHeader} />;
};
