import React, { useState } from 'react';
import styled from 'styled-components';
import { BlotterText } from './BlotterText';

const RoadmapSection = styled.section`
  min-height: 100vh;
  width: 100%;
  padding: 4rem 2rem;
  background: var(--background);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const SectionTitle = styled.div`
  text-align: center;
  margin-bottom: 4rem;
`;

const TimelineContainer = styled.div`
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
  position: relative;
  padding: 2rem 0;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    width: 4px;
    height: 100%;
    background: rgba(0, 255, 157, 0.2);
    border-radius: 2px;
  }

  @media (max-width: 768px) {
    &::before {
      left: 30px;
    }
  }
`;

const PhaseCard = styled.div<{ isLeft: boolean }>`
  width: calc(50% - 50px);
  margin: ${props => props.isLeft ? '0 auto 0 0' : '0 0 0 auto'};
  padding: 2rem;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  position: relative;
  margin-bottom: 4rem;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }

  &::before {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    background: var(--accent);
    border-radius: 50%;
    top: 50%;
    ${props => props.isLeft ? 'right: -60px' : 'left: -60px'};
    transform: translateY(-50%);
  }

  &::after {
    content: '';
    position: absolute;
    width: 40px;
    height: 2px;
    background: var(--accent);
    top: 50%;
    ${props => props.isLeft ? 'right: -40px' : 'left: -40px'};
    transform: translateY(-50%);
  }

  @media (max-width: 768px) {
    width: calc(100% - 80px);
    margin: 0 0 4rem 80px;

    &::before {
      left: -50px;
    }

    &::after {
      left: -30px;
    }
  }
`;

const PhaseTitle = styled.div`
  margin-bottom: 1rem;
`;

const PhaseDate = styled.div`
  color: var(--accent);
  font-size: 1.1rem;
  margin-bottom: 1rem;
`;

const PhaseDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
`;

const MilestoneList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 1rem 0 0;
`;

const Milestone = styled.li`
  color: rgba(255, 255, 255, 0.7);
  padding: 0.5rem 0;
  display: flex;
  align-items: center;

  &::before {
    content: 'ðŸš€';
    margin-right: 0.5rem;
  }
`;

const phases = [
  {
    title: "Phase 1: Launch & Community",
    date: "Q2 2024",
    description: "Establishing the foundation of the Squanchiverse.",
    milestones: [
      "Launch of $SQUANCH token",
      "Community building and social presence",
      "Initial Squanchy AI integration",
      "Marketing campaigns and partnerships"
    ]
  },
  {
    title: "Phase 2: AI Evolution",
    date: "Q3 2024",
    description: "Expanding AI capabilities and utility.",
    milestones: [
      "Advanced Squanchy AI features",
      "Quantum randomizer improvements",
      "Exclusive content platform launch",
      "Community governance implementation"
    ]
  },
  {
    title: "Phase 3: Squanchiverse Expansion",
    date: "Q4 2024",
    description: "Taking the ecosystem to new dimensions.",
    milestones: [
      "Cross-chain integration",
      "NFT marketplace launch",
      "Enhanced AI interactions",
      "Strategic partnerships expansion"
    ]
  },
  {
    title: "Phase 4: Ultimate Squanch",
    date: "2025",
    description: "Achieving interdimensional dominance.",
    milestones: [
      "Metaverse integration",
      "Advanced governance features",
      "Global marketing campaign",
      "Ecosystem expansion"
    ]
  }
];

export const Roadmap: React.FC = () => {
  return (
    <RoadmapSection id="roadmap">
      <SectionTitle>
        <BlotterText 
          text="Roadmap to Interdimensional Dominance"
          fill="rgba(255, 255, 255, 0.9)"
        />
      </SectionTitle>

      <TimelineContainer>
        {phases.map((phase, index) => (
          <PhaseCard key={index} isLeft={index % 2 === 0}>
            <PhaseTitle>
              <BlotterText 
                text={phase.title}
                fill="rgba(255, 255, 255, 0.9)"
              />
            </PhaseTitle>
            <PhaseDate>{phase.date}</PhaseDate>
            <PhaseDescription>{phase.description}</PhaseDescription>
            <MilestoneList>
              {phase.milestones.map((milestone, mIndex) => (
                <Milestone key={mIndex}>{milestone}</Milestone>
              ))}
            </MilestoneList>
          </PhaseCard>
        ))}
      </TimelineContainer>
    </RoadmapSection>
  );
};
