import React, { useState, useEffect } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { config } from '../config';

const noiseAnimation = keyframes`
  0% { transform: translate(0, 0); }
  10% { transform: translate(-5%, -5%); }
  20% { transform: translate(-10%, 5%); }
  30% { transform: translate(5%, -10%); }
  40% { transform: translate(-5%, 15%); }
  50% { transform: translate(-10%, 5%); }
  60% { transform: translate(15%, 0); }
  70% { transform: translate(0, 10%); }
  80% { transform: translate(-15%, 0); }
  90% { transform: translate(10%, 5%); }
  100% { transform: translate(5%, 0); }
`;

const Section = styled.section`
  background-color: black;
  position: relative;
  overflow: hidden;
  min-height: 100vh;
  margin-top: 0;
  padding: 0;

  &::before {
    content: '';
    position: absolute;
    top: -150%;
    left: -150%;
    width: 400%;
    height: 400%;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 3000 3000' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.6' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
    opacity: 0.12;
    animation: ${noiseAnimation} 1s steps(2) infinite;
    pointer-events: none;
  }
`;

const FeaturesContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 6rem 2rem;
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
`;

const QuantumTerminal = styled.div`
  background: rgba(0, 0, 0, 0.7);
  border: 1px solid rgba(255, 215, 0, 0.3);
  border-radius: 15px;
  padding: 3rem;
  color: white;
  backdrop-filter: blur(10px);
  box-shadow: 0 0 20px rgba(255, 215, 0, 0.1);
  width: 100%;
  max-width: 800px;
  min-height: 400px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const glowPulse = keyframes`
  0% { box-shadow: 0 0 20px rgba(128, 0, 255, 0.4); }
  50% { box-shadow: 0 0 40px rgba(128, 0, 255, 0.6); }
  100% { box-shadow: 0 0 20px rgba(128, 0, 255, 0.4); }
`;

const MainButton = styled.button`
  background: linear-gradient(45deg, #4a0080, #8000ff);
  color: white;
  border: none;
  padding: 1.5rem 3rem;
  border-radius: 30px;
  font-size: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
  font-family: 'Creepster', cursive;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  animation: ${glowPulse} 2s infinite;

  &:hover {
    transform: scale(1.05);
    animation: ${glowPulse} 1s infinite;
  }
`;

const TerminalColors = {
  success: '#98c379', // green
  error: '#e06c75',   // red
  warning: '#e5c07b', // yellow
  info: '#61afef',    // blue
  prompt: '#c678dd',  // purple
  path: '#56b6c2',    // cyan
  command: '#abb2bf', // white
  comment: '#5c6370'  // gray
};

const LoadingSequence = styled.div`
  font-family: 'Source Code Pro', monospace;
  font-size: 1rem;
  color: ${TerminalColors.command};
  text-align: left;
  width: 100%;
  margin-bottom: 2rem;
  background: #282c34;
  padding: 1rem;
  border-radius: 8px;
  border: 1px solid #3e4451;
  height: 400px;
  overflow-y: auto;
  position: relative;

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: rgba(171, 178, 191, 0.1);
  }

  &::-webkit-scrollbar-thumb {
    background: rgba(171, 178, 191, 0.3);
    border-radius: 4px;
  }
`;

const ProgressText = styled.div`
  margin: 0.2rem 0;
  opacity: 0.9;
  position: relative;
  padding-left: 1.5rem;
  line-height: 1.4;
  color: ${TerminalColors.command};

  &::before {
    content: '➜';
    position: absolute;
    left: 0;
    color: ${TerminalColors.prompt};
  }
`;

const Spinner = styled.span`
  display: inline-block;
  margin-right: 8px;
  animation: spin 1s steps(10) infinite;

  @keyframes spin {
    0% { content: '⠋'; }
    10% { content: '⠙'; }
    20% { content: '⠹'; }
    30% { content: '⠸'; }
    40% { content: '⠼'; }
    50% { content: '⠴'; }
    60% { content: '⠦'; }
    70% { content: '⠧'; }
    80% { content: '⠇'; }
    90% { content: '⠏'; }
    100% { content: '⠋'; }
  }

  &::after {
    content: '⠋';
  }
`;

const LoadingBar = styled.div`
  font-family: 'Source Code Pro', monospace;
  color: ${TerminalColors.command};
  margin: 0.2rem 0;
  opacity: 0.9;
  white-space: pre;
`;

const ErrorText = styled.div`
  color: ${TerminalColors.error};
  margin: 0.2rem 0;
  padding-left: 1.5rem;
  opacity: 0.9;
`;

const WarningText = styled.div`
  color: ${TerminalColors.warning};
  margin: 0.2rem 0;
  padding-left: 1.5rem;
  opacity: 0.9;
`;

const RetryText = styled.div`
  color: ${TerminalColors.info};
  margin: 0.2rem 0;
  padding-left: 1.5rem;
  opacity: 0.9;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 8px;
  height: 15px;
  background: #0f0;
  margin-left: 4px;
  animation: blink 1s step-end infinite;

  @keyframes blink {
    50% { opacity: 0; }
  }
`;

const ArtifactDisplay = styled.div`
  margin-top: 2rem;
  padding: 2rem;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  border: 1px solid rgba(255, 215, 0, 0.2);
  width: 100%;
`;

const ArtifactImage = styled.img`
  width: 100%;
  max-width: 400px;
  height: auto;
  margin: 2rem auto;
  display: block;
  border-radius: 10px;
  border: 2px solid rgba(128, 0, 255, 0.3);
`;

const ArtifactText = styled.div`
  font-family: 'Creepster', cursive;
  font-size: 1.2rem;
  color: #fff;
  margin: 1rem 0;
  text-align: center;
`;

const packageLoadingStates = [
  { name: "quantum-core", size: "256MB" },
  { name: "dimensional-bridge", size: "512MB" },
  { name: "reality-parser", size: "128MB" },
  { name: "multiverse-scanner", size: "1GB" },
  { name: "artifact-compiler", size: "384MB" },
  { name: "neural-interface", size: "768MB" },
  { name: "quantum-stabilizer", size: "192MB" },
  { name: "entropy-reducer", size: "448MB" },
  { name: "timeline-synchronizer", size: "896MB" },
  { name: "void-mapper", size: "320MB" }
];

interface ChatResponse {
  choices: Array<{
    message: {
      content: string;
    };
  }>;
}

const hackingMessages = [
  "Initializing quantum breach protocol...",
  "Accessing dimensional gateway...",
  "Bypassing security matrix...",
  "Injecting polymorphic code sequence...",
  "Scanning parallel universes...",
  "Decrypting interdimensional protocols...",
  "Loading quantum encryption keys...",
  "Establishing neural network uplink...",
  "Accessing forbidden database sectors...",
  "Compiling dimensional coordinates...",
  "Installing quantum drivers...",
  "Running security bypass...",
  "Initializing reality distortion field...",
  "Loading dimensional translation matrix...",
  "Accessing forbidden knowledge banks...",
  "Injecting consciousness transfer protocols...",
  "Scanning dimensional frequencies...",
  "Calibrating quantum stabilizers...",
  "Loading artifact manifestation drivers...",
  "Compiling interdimensional data streams..."
];

const installationMessages = [
  "Installing quantum visualization drivers...",
  "Downloading artifact materialization modules...",
  "Updating neural pattern recognition...",
  "Installing dimensional calibration tools...",
  "Configuring reality anchors...",
  "Setting up quantum entanglement protocols...",
  "Installing parallel universe scanners...",
  "Updating dimensional drift compensators...",
  "Installing artifact containment systems...",
  "Configuring safety protocol databases..."
];

const processingMessages = [
  "Processing quantum fluctuations...",
  "Analyzing dimensional resonance...",
  "Calculating probability matrices...",
  "Stabilizing reality anchors...",
  "Verifying quantum signatures...",
  "Processing temporal data...",
  "Analyzing artifact composition...",
  "Calculating dimensional stability...",
  "Processing quantum entanglement...",
  "Verifying containment parameters..."
];

const generateRandomHex = () => {
  return Math.random().toString(16).substring(2, 8).toUpperCase();
};

const generateRandomPath = () => {
  const paths = [
    '/quantum/matrix/',
    '/dimensional/gateway/',
    '/neural/network/',
    '/reality/core/',
    '/forbidden/knowledge/'
  ];
  return paths[Math.floor(Math.random() * paths.length)] + generateRandomHex();
};

const loadingSteps = [
  "Generating quantum seed",
  "Activating terminal",
  "Hard booting terminal accelerator",
  "Establishing interdimensional connection",
  "Scanning multiverse frequencies",
  "Bypassing quantum firewall",
  "Accessing artifact database",
  "Stabilizing dimensional rift",
  "Verifying artifact integrity",
  "Materializing quantum data"
];

const generateDimensionalCoordinate = () => {
  const dimension = String.fromCharCode(65 + Math.floor(Math.random() * 26));
  const number = Math.floor(Math.random() * 1000);
  const subDimension = Math.floor(Math.random() * 100);
  return `${dimension}-${number}.${subDimension}`;
};

interface PackageState {
  name: string;
  size: string;
  progress: number;
  speed: string;
  status: 'downloading' | 'error' | 'retry' | 'warning' | 'success';
  attempts: number;
}

export const InteractiveFeatures: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [loadingProgress, setLoadingProgress] = useState<string[]>([]);
  const [hackingOutput, setHackingOutput] = useState<string[]>([]);
  const [artifactData, setArtifactData] = useState<{
    prediction: string;
    survivalTip: string;
    imageUrl: string;
  } | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [installProgress, setInstallProgress] = useState<{ message: string; progress: number }[]>([]);
  const [packageLoading, setPackageLoading] = useState<{
    name: string;
    progress: number;
    size: string;
    speed: string;
  } | null>(null);
  const [packageStates, setPackageStates] = useState<PackageState[]>([]);
  const [spinnerIndex, setSpinnerIndex] = useState(0);

  useEffect(() => {
    if (loading && currentStep < loadingSteps.length) {
      // Main loading step
      const timer = setTimeout(() => {
        const coordinate = generateDimensionalCoordinate();
        const stepText = currentStep === 1 
          ? `${loadingSteps[currentStep]} ${coordinate}`
          : loadingSteps[currentStep];

        setLoadingProgress(prev => [...prev, `${stepText}...${(currentStep + 1) * 10}%`]);
        setCurrentStep(prev => prev + 1);

        if (currentStep === loadingSteps.length - 1) {
          setIsGenerating(true);
          generateArtifact();
        }
      }, 1000);

      // Random hacking messages
      const hackingTimer = setInterval(() => {
        const randomMessage = hackingMessages[Math.floor(Math.random() * hackingMessages.length)];
        const path = generateRandomPath();
        setHackingOutput(prev => [...prev, `[${path}] ${randomMessage}`].slice(-20));
      }, 200);

      return () => {
        clearTimeout(timer);
        clearInterval(hackingTimer);
      };
    }
  }, [loading, currentStep]);

  // Keep terminal active during generation
  useEffect(() => {
    if (isGenerating) {
      // Installation progress
      const installTimer = setInterval(() => {
        const message = installationMessages[Math.floor(Math.random() * installationMessages.length)];
        const progress = Math.floor(Math.random() * 100);
        setInstallProgress(prev => [...prev.slice(-5), { message, progress }]);
      }, 800);

      // Processing messages
      const processTimer = setInterval(() => {
        const message = processingMessages[Math.floor(Math.random() * processingMessages.length)];
        setHackingOutput(prev => [...prev, `[PROCESS] ${message}`].slice(-20));
      }, 300);

      return () => {
        clearInterval(installTimer);
        clearInterval(processTimer);
      };
    }
  }, [isGenerating]);

  // Add package loading effect
  useEffect(() => {
    if (isGenerating) {
      let currentPackageIndex = 0;
      let currentAttempt = 1;

      const simulatePackageInstall = () => {
        const pkg = packageLoadingStates[currentPackageIndex];
        const speed = `${Math.floor(Math.random() * 100) + 50}MB/s`;
        const shouldError = Math.random() < 0.2; // 20% chance of error
        const shouldWarn = Math.random() < 0.15; // 15% chance of warning

        setPackageStates(prev => {
          const newStates = [...prev];
          const existingPkg = newStates.find(p => p.name === pkg.name);

          if (existingPkg) {
            if (shouldError && existingPkg.progress < 90) {
              existingPkg.status = 'error';
              existingPkg.attempts += 1;
            } else if (shouldWarn) {
              existingPkg.status = 'warning';
              existingPkg.progress += Math.floor(Math.random() * 5);
            } else {
              existingPkg.status = 'downloading';
              existingPkg.progress += Math.floor(Math.random() * 10) + 5;
              existingPkg.speed = speed;
            }

            if (existingPkg.progress >= 100) {
              existingPkg.status = 'success';
              existingPkg.progress = 100;
            }
          } else {
            newStates.push({
              name: pkg.name,
              size: pkg.size,
              progress: 0,
              speed,
              status: 'downloading',
              attempts: 1
            });
          }
          return newStates;
        });

        setSpinnerIndex(prev => (prev + 1) % 10);
      };

      const packageTimer = setInterval(() => {
        simulatePackageInstall();

        // Check if current package is complete
        const currentPkg = packageStates.find(p => p.name === packageLoadingStates[currentPackageIndex].name);
        if (currentPkg?.status === 'success') {
          currentPackageIndex = (currentPackageIndex + 1) % packageLoadingStates.length;
          currentAttempt = 1;
        }
      }, 150);

      return () => clearInterval(packageTimer);
    } else {
      setPackageStates([]);
    }
  }, [isGenerating, packageStates]);

  const makeOpenAIRequest = async (messages: Array<{ role: string; content: string }>, maxTokens: number) => {
    const apiKey = config.openaiApiKey;

    if (!apiKey) {
      throw new Error('OpenAI API key is not configured');
    }

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: 'gpt-4',
          messages,
          max_tokens: maxTokens
        })
      });

      if (!response.ok) {
        const errorData = await response.text();
        console.error('OpenAI API Error:', {
          status: response.status,
          statusText: response.statusText,
          error: errorData
        });
        throw new Error(`OpenAI API error: ${response.status} ${response.statusText}`);
      }

      const data: ChatResponse = await response.json();
      return data.choices[0]?.message?.content || '';
    } catch (error) {
      console.error('Request error:', error);
      throw error;
    }
  };

  const generateImage = async (prompt: string) => {
    const apiKey = config.openaiApiKey;

    if (!apiKey) {
      throw new Error('OpenAI API key is not configured');
    }

    const enhancedPrompt = `Create a highly detailed, Rick and Morty style interdimensional artifact. The artifact should be a strange, alien device or object with glowing elements and intricate mechanical details. Style: Vibrant colors, sci-fi aesthetic, detailed mechanical parts. Important: The image must not contain any text, numbers, symbols, or writing of any kind. The image should focus purely on the visual appearance of the artifact without any textual elements.`;

    const negativePrompt = "text, writing, numbers, symbols, letters, characters, words, labels, signs, watermarks, signatures, logos";

    try {
      const response = await fetch('https://api.openai.com/v1/images/generations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "dall-e-3",
          prompt: enhancedPrompt,
          n: 1,
          size: "1024x1024",
          quality: "hd",
          style: "vivid"
        })
      });

      if (!response.ok) {
        throw new Error(`Image generation failed: ${response.statusText}`);
      }

      const data = await response.json();
      return data.data[0].url;
    } catch (error) {
      console.error('Error generating image:', error);
      throw error;
    }
  };

  const generateArtifact = async () => {
    try {
      const [prediction, survivalTip] = await Promise.all([
        makeOpenAIRequest([{
          role: "system",
          content: "You are an interdimensional artifact analyzer providing quirky, Rick and Morty-style analysis. Describe a weird, unique interdimensional artifact that could exist in the Rick and Morty universe. Keep responses concise (2-3 sentences), entertaining, and mysterious. Focus on the physical appearance and strange properties of the artifact."
        }, {
          role: "user",
          content: "Generate a random interdimensional artifact analysis or prediction."
        }], 100),
        makeOpenAIRequest([{
          role: "system",
          content: "You are an advanced AI providing survival tips related to interdimensional artifacts. Include specific warnings about handling the artifact. Keep it in the style of Rick and Morty."
        }, {
          role: "user",
          content: "Generate a survival tip for handling an interdimensional artifact."
        }], 150)
      ]);

      const imageUrl = await generateImage(prediction);

      setArtifactData({
        prediction,
        survivalTip,
        imageUrl
      });
    } catch (error) {
      console.error('Error generating artifact:', error);
      setArtifactData({
        prediction: 'Error connecting to the multiverse. Please try again.',
        survivalTip: 'Connection lost. Unable to retrieve safety protocols.',
        imageUrl: ''
      });
    } finally {
      setIsGenerating(false);
      setLoading(false);
    }
  };

  const startGeneration = () => {
    setLoading(true);
    setCurrentStep(0);
    setLoadingProgress([]);
    setArtifactData(null);
  };

  const renderPackageStatus = (pkg: PackageState) => {
    switch (pkg.status) {
      case 'error':
        return (
          <>
            <ErrorText>✗ Failed to install {pkg.name} (Attempt {pkg.attempts})</ErrorText>
            <RetryText>↺ Retrying installation...</RetryText>
          </>
        );
      case 'warning':
        return (
          <WarningText>⚠ Unstable quantum fluctuations detected in {pkg.name}</WarningText>
        );
      case 'success':
        return (
          <ProgressText style={{ color: TerminalColors.success }}>
            ✓ Successfully installed {pkg.name} [{'.'.repeat(20)}] 100%
          </ProgressText>
        );
      default:
        return (
          <LoadingBar>
            <Spinner />
            Installing {pkg.name} [{'.'.repeat(Math.floor(pkg.progress/5))}] {pkg.progress}%
            <div style={{ paddingLeft: '24px', fontSize: '0.9em', opacity: 0.7, color: TerminalColors.comment }}>
              Size: {pkg.size} | Speed: {pkg.speed}
            </div>
          </LoadingBar>
        );
    }
  };

  return (
    <Section>
      <FeaturesContainer>
        <QuantumTerminal>
          {!loading && !artifactData && (
            <MainButton onClick={startGeneration}>
              Generate Quantum Squanch Artifact
            </MainButton>
          )}

          {(loading || isGenerating) && (
            <LoadingSequence>
              {loadingProgress.map((step, index) => (
                <ProgressText key={`progress-${index}`}>{step}</ProgressText>
              ))}
              {isGenerating && (
                <>
                  {packageStates.map((pkg, index) => (
                    <React.Fragment key={`pkg-${index}`}>
                      {renderPackageStatus(pkg)}
                    </React.Fragment>
                  ))}
                </>
              )}
              {hackingOutput.map((output, index) => (
                <ProgressText key={`hacking-${index}`} style={{ color: '#00ff00', opacity: 0.6 }}>
                  {output}
                </ProgressText>
              ))}
              <Cursor />
            </LoadingSequence>
          )}

          {artifactData && !loading && !isGenerating && (
            <ArtifactDisplay>
              <ArtifactText>{artifactData.prediction}</ArtifactText>
              <ArtifactImage src={artifactData.imageUrl} alt="Quantum Artifact" />
              <ArtifactText>⚠ Safety Protocol:</ArtifactText>
              <ArtifactText>{artifactData.survivalTip}</ArtifactText>
            </ArtifactDisplay>
          )}
        </QuantumTerminal>
      </FeaturesContainer>
    </Section>
  );
};
