import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';

declare global {
  interface Window {
    Blotter: any;
    blotterReady: boolean;
  }
}

interface LoadingTextProps {
  $size: number;
  $fill: string;
}

const TextContainer = styled.div`
  canvas {
    width: 100% !important;
    height: auto !important;
  }
`;

const LoadingText = styled.div<LoadingTextProps>`
  font-size: ${props => props.$size}px;
  color: ${props => props.$fill};
  text-align: center;
  font-family: 'Creepster', cursive;
`;

interface BlotterTextProps {
  text: string;
  size?: number;
  fill?: string;
  className?: string;
}

const MathUtils = {
  lineEq: (y2: number, y1: number, x2: number, x1: number, currentVal: number) => {
    const m = (y2 - y1) / (x2 - x1);
    const b = y1 - m * x1;
    return m * currentVal + b;
  },
  lerp: (a: number, b: number, n: number) => (1 - n) * a + n * b,
  distance: (x1: number, x2: number, y1: number, y2: number) => {
    const a = x1 - x2;
    const b = y1 - y2;
    return Math.hypot(a, b);
  }
};

export const BlotterText: React.FC<BlotterTextProps> = ({ 
  text, 
  size = 100,
  fill = '#fff',
  className 
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const materialRef = useRef<any>(null);
  const blotterRef = useRef<any>(null);
  const [isLoading, setIsLoading] = useState(true);
  const lastMousePosition = useRef({ x: window.innerWidth/2, y: window.innerHeight/2 });
  const volatilityRef = useRef(0);
  const animationFrameRef = useRef<number>();
  const mousePosRef = useRef({ x: window.innerWidth/2, y: window.innerHeight/2 });
  const winsizeRef = useRef({ width: window.innerWidth, height: window.innerHeight });

  // Calculate window size
  const calcWinsize = () => {
    winsizeRef.current = {
      width: window.innerWidth,
      height: window.innerHeight
    };
  };

  // Get mouse position
  const getMousePos = (ev: MouseEvent) => {
    const docEl = document.documentElement;
    const body = document.body;
    let posx = 0;
    let posy = 0;

    if (ev.pageX || ev.pageY) {
      posx = ev.pageX;
      posy = ev.pageY;
    } else if (ev.clientX || ev.clientY) {
      posx = ev.clientX + body.scrollLeft + docEl.scrollLeft;
      posy = ev.clientY + body.scrollTop + docEl.scrollTop;
    }
    return { x: posx, y: posy };
  };

  const handleMouseMove = (ev: MouseEvent) => {
    mousePosRef.current = getMousePos(ev);
  };

  useEffect(() => {
    let checkAttempts = 0;
    const maxAttempts = 50;

    const initBlotter = () => {
      try {
        console.log('Creating material');
        const material = new window.Blotter.LiquidDistortMaterial();
        materialRef.current = material;

        // Set initial uniforms
        material.uniforms.uSpeed.value = 1.0;
        material.uniforms.uVolatility.value = 0;
        material.uniforms.uSeed.value = 0.1;

        console.log('Creating Blotter instance');
        const blotter = new window.Blotter(material, {
          texts: [
            new window.Blotter.Text(text, {
              family: "Creepster",
              size: size,
              fill: fill,
              paddingLeft: 100,
              paddingRight: 100,
              weight: 400,
              leading: 1,
              tracking: 0,
            }),
          ],
        });

        blotterRef.current = blotter;

        console.log('Getting scope');
        const scope = blotter.forText(blotter.texts[0]);

        if (containerRef.current) {
          console.log('Appending to container');
          while (containerRef.current.firstChild) {
            containerRef.current.removeChild(containerRef.current.firstChild);
          }
          containerRef.current.appendChild(scope.domElement);
        }

        // Animation setup
        const render = () => {
          if (!materialRef.current) return;

          const docEl = document.documentElement;
          const body = document.body;
          const docScrolls = {
            left: body.scrollLeft + docEl.scrollLeft,
            top: body.scrollTop + docEl.scrollTop
          };

          // Get relative mouse position
          const relmousepos = {
            x: mousePosRef.current.x - docScrolls.left,
            y: mousePosRef.current.y - docScrolls.top
          };

          // Calculate mouse movement distance
          const mouseDistance = MathUtils.distance(
            lastMousePosition.current.x,
            relmousepos.x,
            lastMousePosition.current.y,
            relmousepos.y
          );

          // Update volatility based on mouse movement
          volatilityRef.current = MathUtils.lerp(
            volatilityRef.current,
            Math.min(MathUtils.lineEq(0.9, 0, 100, 0, mouseDistance), 0.9),
            0.05
          );

          // Apply the effect
          material.uniforms.uVolatility.value = volatilityRef.current;

          // Store last mouse position
          lastMousePosition.current = { x: relmousepos.x, y: relmousepos.y };

          // Continue animation loop
          animationFrameRef.current = requestAnimationFrame(render);
        };

        console.log('Starting animation');
        animationFrameRef.current = requestAnimationFrame(render);

        // Event listeners
        window.addEventListener('mousemove', handleMouseMove);
        window.addEventListener('resize', calcWinsize);

        // Initial calculations
        calcWinsize();

        console.log('Blotter initialization complete');
      } catch (error) {
        console.error('Error initializing Blotter:', error);
        setIsLoading(true);
      }
    };

    const checkBlotter = () => {
      checkAttempts++;
      if (window.blotterReady && window.Blotter && window.Blotter.LiquidDistortMaterial) {
        console.log('Blotter initialized successfully');
        setIsLoading(false);
        initBlotter();
      } else if (checkAttempts < maxAttempts) {
        setTimeout(checkBlotter, 100);
      } else {
        console.error('Failed to load Blotter after 5 seconds');
        setIsLoading(true);
      }
    };

    setTimeout(checkBlotter, 500);

    return () => {
      checkAttempts = maxAttempts;
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', calcWinsize);
      if (blotterRef.current) {
        blotterRef.current.destroy();
      }
    };
  }, [text, size, fill]);

  if (isLoading) {
    return <LoadingText $size={size} $fill={fill}>{text}</LoadingText>;
  }

  return <TextContainer ref={containerRef} className={className} />;
};
