import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';

declare global {
  interface Window {
    Blotter: any;
  }
}

interface LoadingTextProps {
  $size: number;
  $fill: string;
}

const TextContainer = styled.div`
  canvas {
    width: 100% !important;
    height: auto !important;
  }
`;

const LoadingText = styled.div<LoadingTextProps>`
  font-size: ${props => props.$size}px;
  color: ${props => props.$fill};
  text-align: center;
  font-family: serif;
`;

interface BlotterTextProps {
  text: string;
  size?: number;
  fill?: string;
  className?: string;
}

const MathUtils = {
  lineEq: (y2: number, y1: number, x2: number, x1: number, currentVal: number) => {
    const m = (y2 - y1) / (x2 - x1);
    const b = y1 - m * x1;
    return m * currentVal + b;
  },
  lerp: (a: number, b: number, n: number) => (1 - n) * a + n * b,
};

export const BlotterText: React.FC<BlotterTextProps> = ({ 
  text, 
  size = 100,
  fill = '#fff',
  className 
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const materialRef = useRef<any>(null);
  const blotterRef = useRef<any>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkBlotter = () => {
      if (window.Blotter) {
        setIsLoading(false);
        initBlotter();
      } else {
        setTimeout(checkBlotter, 100);
      }
    };

    const initBlotter = () => {
      const material = new window.Blotter.LiquidDistortMaterial();
      materialRef.current = material;

      material.uniforms.uSpeed.value = 0.5;
      material.uniforms.uVolatility.value = 0;
      material.uniforms.uSeed.value = 0.4;

      const blotter = new window.Blotter(material, {
        texts: [
          new window.Blotter.Text(text, {
            family: 'serif',
            size: size,
            fill: fill,
            paddingLeft: 100,
            paddingRight: 100,
          }),
        ],
      });

      blotterRef.current = blotter;

      const scope = blotter.forText(blotter.texts[0]);
      if (containerRef.current) {
        // Clear any existing content
        while (containerRef.current.firstChild) {
          containerRef.current.removeChild(containerRef.current.firstChild);
        }
        containerRef.current.appendChild(scope.domElement);
      }

      let currentScroll = window.pageYOffset;
      let volatility = 0;
      const maxscroll = 10;
      const uniformValuesRange = [0, 0.9];

      const handleMouseMove = (e: MouseEvent) => {
        const mouseDistanceFromCenter = Math.sqrt(
          Math.pow(e.clientX - window.innerWidth / 2, 2) +
          Math.pow(e.clientY - window.innerHeight / 2, 2)
        );
        const maxDistance = Math.sqrt(
          Math.pow(window.innerWidth / 2, 2) +
          Math.pow(window.innerHeight / 2, 2)
        );
        const mouseInfluence = mouseDistanceFromCenter / maxDistance;
        material.uniforms.uVolatility.value = mouseInfluence * 0.5;
      };

      const render = () => {
        const newScroll = window.pageYOffset;
        const scrolled = Math.abs(newScroll - currentScroll);
        volatility = MathUtils.lerp(
          volatility,
          Math.min(
            MathUtils.lineEq(
              uniformValuesRange[1],
              uniformValuesRange[0],
              maxscroll,
              0,
              scrolled
            ),
            0.9
          ),
          0.05
        );
        material.uniforms.uVolatility.value = volatility;
        currentScroll = newScroll;
        requestAnimationFrame(render);
      };

      window.addEventListener('mousemove', handleMouseMove);
      requestAnimationFrame(render);

      return () => {
        window.removeEventListener('mousemove', handleMouseMove);
        if (containerRef.current?.firstChild) {
          containerRef.current.removeChild(containerRef.current.firstChild);
        }
        blotter.destroy();
      };
    };

    checkBlotter();
  }, [text, size, fill]);

  if (isLoading) {
    return <LoadingText $size={size} $fill={fill}>{text}</LoadingText>;
  }

  return <TextContainer ref={containerRef} className={className} />;
}
