import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import * as THREE from 'three';

const TextContainer = styled.div<{ $isHeader?: boolean }>`
  width: 100%;
  height: ${props => props.$isHeader ? '100vh' : 'auto'};
  display: flex;
  align-items: center;
  justify-content: center;
  canvas {
    width: 100% !important;
    height: ${props => props.$isHeader ? '100vh' : 'auto'} !important;
    object-fit: contain;
  }
`;

const FallbackText = styled.div<{ $isHeader?: boolean }>`
  width: 100%;
  height: ${props => props.$isHeader ? '100vh' : 'auto'};
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'Creepster', cursive;
  font-size: ${props => props.$isHeader ? '4rem' : '2rem'};
  color: ${props => props.color || '#fff'};
`;

interface BlotterTextProps {
  text: string;
  size?: number;
  fill?: string;
  className?: string;
  isHeader?: boolean;
}

const vertexShader = `
  varying vec2 vUv;
  void main() {
    vUv = uv;
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
  }
`;

const fragmentShader = `
  uniform float time;
  uniform float distortionFactor;
  uniform vec2 mousePos;
  uniform vec3 color;
  uniform sampler2D textTexture;
  varying vec2 vUv;

  void main() {
    vec2 uv = vUv;

    // Calculate distance from mouse with larger radius of influence
    float dist = distance(mousePos, uv);
    float influence = smoothstep(0.8, 0.0, dist) * distortionFactor;

    // Create wave effect with increased amplitude
    float wave = sin(uv.x * 8.0 + time) * 0.08 * influence;
    uv.y += wave;

    // Create liquid-like distortion with smoother movement
    float distortion = sin(uv.x * 4.0 + time * 1.5) * cos(uv.y * 4.0 + time) * influence;
    uv += distortion * 0.08;

    // Sample the text texture
    vec4 texColor = texture2D(textTexture, uv);

    // Mix the text color with our desired color
    gl_FragColor = vec4(color * texColor.r, texColor.a);
  }
`;

// Check if WebGL is available
const isWebGLAvailable = () => {
  try {
    const canvas = document.createElement('canvas');
    return !!(window.WebGLRenderingContext && 
      (canvas.getContext('webgl') || canvas.getContext('experimental-webgl')));
  } catch (e) {
    return false;
  }
};

export const BlotterText: React.FC<BlotterTextProps> = ({ 
  text, 
  size = 100,
  fill = '#fff',
  className,
  isHeader = false
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const rendererRef = useRef<THREE.WebGLRenderer | null>(null);
  const sceneRef = useRef<THREE.Scene | null>(null);
  const cameraRef = useRef<THREE.OrthographicCamera | null>(null);
  const materialRef = useRef<THREE.ShaderMaterial | null>(null);
  const mousePos = useRef<{ x: number; y: number }>({ x: 0.5, y: 0.5 });
  const frameRef = useRef<number>();
  const [hasWebGLError, setHasWebGLError] = useState(false);

  useEffect(() => {
    if (!containerRef.current || !isWebGLAvailable()) {
      setHasWebGLError(true);
      return;
    }

    try {
      // Setup Three.js scene
      const scene = new THREE.Scene();
      sceneRef.current = scene;

      // Setup camera
      const camera = new THREE.OrthographicCamera(-1, 1, 1, -1, 0.1, 10);
      camera.position.z = 1;
      cameraRef.current = camera;

      // Setup renderer with error handling
      let renderer: THREE.WebGLRenderer;
      try {
        renderer = new THREE.WebGLRenderer({ 
          antialias: true, 
          alpha: true,
          powerPreference: 'default'
        });
      } catch (e) {
        console.error('WebGL renderer creation failed:', e);
        setHasWebGLError(true);
        return;
      }

      renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2)); // Limit pixel ratio to avoid performance issues
      renderer.setSize(containerRef.current.clientWidth, containerRef.current.clientHeight);
      rendererRef.current = renderer;

      // Clear container
      while (containerRef.current.firstChild) {
        containerRef.current.removeChild(containerRef.current.firstChild);
      }
      containerRef.current.appendChild(renderer.domElement);

      // Create text texture
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      if (!ctx) {
        setHasWebGLError(true);
        return;
      }

      // Set canvas size based on container size to maintain aspect ratio
      const containerAspect = containerRef.current.clientWidth / containerRef.current.clientHeight;
      canvas.width = Math.min(2048, containerRef.current.clientWidth * 2); // Limit max texture size
      canvas.height = canvas.width / containerAspect;

      ctx.fillStyle = 'black';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Calculate font size based on whether it's a header or not
      const fontSize = isHeader 
        ? Math.floor(canvas.height * 0.25)
        : Math.floor(canvas.height * 0.8);

      ctx.font = `bold ${fontSize}px Creepster`;
      ctx.fillStyle = 'white';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';

      // Measure text width to handle long text
      const textMetrics = ctx.measureText(text);
      const textWidth = textMetrics.width;
      const maxWidth = canvas.width * 0.9;

      // Scale text if it's too wide
      if (textWidth > maxWidth) {
        const scale = maxWidth / textWidth;
        ctx.font = `bold ${Math.floor(fontSize * scale)}px Creepster`;
      }

      // Draw text in the center
      ctx.fillText(text, canvas.width / 2, canvas.height / 2);

      const texture = new THREE.CanvasTexture(canvas);
      texture.needsUpdate = true;

      // Create shader material
      const material = new THREE.ShaderMaterial({
        uniforms: {
          time: { value: 0 },
          distortionFactor: { value: 0 },
          mousePos: { value: new THREE.Vector2(0.5, 0.5) },
          color: { value: new THREE.Color(fill) },
          textTexture: { value: texture }
        },
        vertexShader,
        fragmentShader,
        transparent: true
      });
      materialRef.current = material;

      // Create mesh with proper aspect ratio
      const geometry = new THREE.PlaneGeometry(2, 2 / containerAspect);
      const mesh = new THREE.Mesh(geometry, material);
      scene.add(mesh);

      // Animation loop with error handling
      const animate = () => {
        try {
          if (!material || !renderer || !scene || !camera) return;

          material.uniforms.time.value += 0.01;
          material.uniforms.mousePos.value.set(mousePos.current.x, mousePos.current.y);

          renderer.render(scene, camera);
          frameRef.current = requestAnimationFrame(animate);
        } catch (e) {
          console.error('Animation error:', e);
          setHasWebGLError(true);
        }
      };

      frameRef.current = requestAnimationFrame(animate);

      // Handle mouse movement
      const handleMouseMove = (e: MouseEvent) => {
        const rect = containerRef.current?.getBoundingClientRect();
        if (!rect) return;

        mousePos.current = {
          x: (e.clientX - rect.left) / rect.width,
          y: 1 - (e.clientY - rect.top) / rect.height
        };

        if (materialRef.current) {
          const dist = Math.hypot(
            mousePos.current.x - 0.5,
            mousePos.current.y - 0.5
          );
          materialRef.current.uniforms.distortionFactor.value = Math.min(dist * 2, 1);
        }
      };

      window.addEventListener('mousemove', handleMouseMove);

      // Handle resize
      const handleResize = () => {
        if (!containerRef.current || !renderer || !camera) return;

        const newWidth = containerRef.current.clientWidth;
        const newHeight = isHeader ? window.innerHeight : containerRef.current.clientHeight;
        const newAspect = newWidth / newHeight;

        renderer.setSize(newWidth, newHeight);

        // Update camera aspect
        camera.left = -1;
        camera.right = 1;
        camera.top = 1 / newAspect;
        camera.bottom = -1 / newAspect;
        camera.updateProjectionMatrix();
      };

      window.addEventListener('resize', handleResize);
      handleResize(); // Initial resize

      return () => {
        if (frameRef.current) {
          cancelAnimationFrame(frameRef.current);
        }
        window.removeEventListener('mousemove', handleMouseMove);
        window.removeEventListener('resize', handleResize);
        renderer.dispose();
      };
    } catch (e) {
      console.error('Setup error:', e);
      setHasWebGLError(true);
    }
  }, [text, size, fill, isHeader]);

  if (hasWebGLError) {
    return (
      <FallbackText 
        $isHeader={isHeader} 
        className={className}
        color={fill}
      >
        {text}
      </FallbackText>
    );
  }

  return <TextContainer ref={containerRef} className={className} $isHeader={isHeader} />;
};
