import React from 'react';
import styled from 'styled-components';
import { BlotterText } from './BlotterText';

const FeaturesSection = styled.section`
  min-height: 100vh;
  width: 100%;
  padding: 4rem 2rem;
  background: var(--background);
`;

const SectionTitle = styled.div`
  text-align: center;
  margin-bottom: 4rem;
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const FeatureCard = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const FeatureImage = styled.div`
  width: 200px;
  height: 200px;
  margin-bottom: 1.5rem;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  // Placeholder for actual images
  &::after {
    content: 'Image';
    color: rgba(255, 255, 255, 0.5);
  }
`;

const FeatureTitle = styled.div`
  margin-bottom: 1rem;
`;

const FeatureDescription = styled.p`
  color: rgba(255, 255, 255, 0.7);
  margin-bottom: 1.5rem;
  line-height: 1.6;
`;

const FeatureButton = styled.a`
  padding: 0.8rem 1.5rem;
  border: 2px solid var(--accent);
  border-radius: 6px;
  color: var(--accent);
  text-decoration: none;
  transition: all 0.3s ease;
  margin-top: auto;

  &:hover {
    background: var(--accent);
    color: var(--background);
  }
`;

const features = [
  {
    title: "Squanchy AI Chat Companion",
    description: "Ask Squanchy anything! Crypto tips, memeology, or just a good squanchy laugh.",
    cta: "Talk to Squanchy!",
    link: "#chat"
  },
  {
    title: "Quantum Randomizer",
    description: "Dive into the multiverse for your personalized prediction or Squanchy-certified advice.",
    cta: "Generate Your Squanch!",
    link: "#randomizer"
  },
  {
    title: "Exclusive Squanchiverse Content",
    description: "Unlock tales of the Squanchiverse, cosmic guides, and more—exclusive to token holders.",
    cta: "Discover the Squanchiverse",
    link: "#content"
  },
  {
    title: "Squanchification Tool",
    description: "Turn your boring sentences into chaotic Squanchy magic. Share and spread the squanch!",
    cta: "Squanchify Now!",
    link: "#squanchify"
  }
];

export const Features: React.FC = () => {
  return (
    <FeaturesSection id="features">
      <SectionTitle>
        <BlotterText 
          text="What's the Squanchiverse?"
          fill="rgba(255, 255, 255, 0.9)"
        />
      </SectionTitle>

      <FeaturesGrid>
        {features.map((feature, index) => (
          <FeatureCard key={index}>
            <FeatureImage />
            <FeatureTitle>
              <BlotterText 
                text={feature.title}
                fill="rgba(255, 255, 255, 0.9)"
              />
            </FeatureTitle>
            <FeatureDescription>{feature.description}</FeatureDescription>
            <FeatureButton href={feature.link}>{feature.cta}</FeatureButton>
          </FeatureCard>
        ))}
      </FeaturesGrid>
    </FeaturesSection>
  );
};
