import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import * as THREE from 'three';

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: -1;
  canvas {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
  }
`;

const portalVertexShader = `
  varying vec2 vUv;
  varying vec3 vPosition;

  void main() {
    vUv = uv;
    vPosition = position;
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
  }
`;

const portalFragmentShader = `
  uniform float time;
  uniform vec2 mousePos;
  uniform vec2 mouseVelocity;
  varying vec2 vUv;
  varying vec3 vPosition;

  #define PI 3.14159265359

  // Noise functions
  vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
  vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
  vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

  float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i);
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
      + i.x + vec3(0.0, i1.x, 1.0 ));
    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
  }

  float fbm(vec2 p) {
    float sum = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    // Reduced octaves for simpler effect
    for(int i = 0; i < 4; i++) {
      sum += amp * snoise(p * freq);
      freq *= 2.0;
      amp *= 0.75;
      p = p * 1.2 + vec2(3.2);
    }
    return sum;
  }

  // Star field function
  float star(vec2 uv, float flare) {
    float d = length(uv);
    float m = 0.001/d;  // Reduced from 0.002 for smaller stars
    return smoothstep(0.9, 1.0, m);  // Removed glow and flare effects
  }

  vec2 rotate(vec2 p, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return vec2(p.x * c - p.y * s, p.x * s + p.y * c);
  }

  vec3 purpleToRed(vec3 purple, float influence) {
    vec3 red = vec3(0.9, 0.1, 0.8);
    // Create inward flowing red effect
    float flowFactor = smoothstep(0.5, 0.0, length(vUv - vec2(0.5)));
    return mix(purple, red, influence * flowFactor);
  }

  float ripple(vec2 uv, vec2 center, float time, float frequency, float speed) {
    float d = length(uv - center);
    // Add inward flow to ripples
    float inwardFlow = (1.0 - d) * 2.0;
    float ripple = sin(d * frequency - time * speed + inwardFlow);
    float fadeOut = smoothstep(1.0, 0.0, d); // Tighter radius
    return fadeOut * ripple;
