    float mouseInfluence = smoothstep(0.12, 0.0, mouseDist); // Even smaller radius

    // Create inward-flowing ripple effect
    float rippleEffect = ripple(vUv, mousePos, time * 5.0, 40.0, 5.0);
    rippleEffect += ripple(vUv, mousePos, time * 4.0, 30.0, 4.0) * 0.5;
    rippleEffect += ripple(vUv, mousePos, time * 3.0, 20.0, 3.0) * 0.25;

    // Create inward flow direction
    vec2 toCenter = normalize(-p);
    float flowStrength = smoothstep(1.0, 0.0, centerDist);

    // Apply ripple distortion with inward flow
    vec2 distortion = vec2(rippleEffect) * mouseInfluence * 0.02;
    distortion += toCenter * mouseInfluence * flowStrength * 0.03; // Add inward pull
    p += distortion;

    float r = length(p);
    float angle = atan(p.y, p.x);

    // Add flow-based turbulence
    float turbulence = mouseInfluence * rippleEffect * flowStrength * 0.02;
    r += turbulence;

