    float energy = flow + noise1 * 0.9;
    energy *= smoothstep(0.0, 0.2, r);
    energy *= smoothstep(1.0, 0.3, r);
    energy += mouseInfluence * rippleEffect * flowStrength * 0.3;

    // Color palette with inward-flowing red
    vec3 darkVoid = vec3(0.0, 0.0, 0.0);
    vec3 baseColor1 = vec3(0.6, 0.0, 0.9);
    vec3 baseColor2 = vec3(0.4, 0.0, 0.7);

    // Create red influence that flows inward
    float redInfluence = mouseInfluence * rippleEffect * smoothstep(1.0, 0.0, centerDist);
    vec3 energyColor1 = purpleToRed(baseColor1, redInfluence);
    vec3 energyColor2 = purpleToRed(baseColor2, redInfluence * 0.8);

    // Create base spiral structure
    float spiralIntensity = smoothstep(0.2, 0.8, energy);
    vec3 finalColor = mix(darkVoid, energyColor1, spiralIntensity * (2.0 - r));

    // Add layered energy effects
    float energyMix1 = (noise1 * 0.5 + redInfluence * 0.3) * (1.0 - r);
    float energyMix2 = (noise2 * 0.3 + redInfluence * 0.2) * (1.0 - r);

    finalColor = mix(finalColor, energyColor1, energyMix1);
    finalColor = mix(finalColor, energyColor2, energyMix2);

