    float starTime = -time * 0.5;
    float starLayer1 = 0.0;
    float starLayer2 = 0.0;

    // Create multiple star layers
    for(int i = 0; i < 35; i++) {
      float fi = float(i);
      float t = starTime * (5.0 + fi * 0.1) + fi * 1.23;
      float scale = 0.2 + fi * 0.05;

      // Star position with spiral motion
      float radius = fract(t * 0.1 + fi * 0.123) * 1.2;
      float angle = -t * 0.5 + fi * 1.57;
      vec2 xy = vec2(cos(angle), sin(angle)) * radius;

      vec2 starPos = rotate(xy, -t * 0.2);
      starPos = starPos * scale;

      float starIntensity = star(p - starPos, 0.15) * 10.0;

      float fadeOut = smoothstep(0.0, 0.1, length(starPos));
      float fadeIn = smoothstep(1.0, 0.3, length(starPos));

      starLayer1 += starIntensity * fadeOut * fadeIn * 0.3;
    }

    // Second star layer with different timing
    for(int i = 0; i < 25; i++) {
      float fi = float(i);
      float t = starTime * (0.8 + fi * 0.1) + fi * 2.34;
      float scale = 5.15 + fi * 0.03;

      float radius = fract(t * 0.15 + fi * 0.234) * 1.0;
      float angle = -t * 0.7 + fi * 2.12;
      vec2 xy = vec2(cos(angle), sin(angle)) * radius;

      vec2 starPos = rotate(xy, -t * 0.25);
      starPos = starPos * scale;

      float starIntensity = star(p - starPos, 0.1) * 1.5;
      float fadeOut = smoothstep(0.0, 0.15, length(starPos));
      float fadeIn = smoothstep(1.0, 0.4, length(starPos));

      starLayer2 += starIntensity * fadeOut * fadeIn * 0.25;
    }

    // Add stars with reduced intensity
    vec3 starColor1 = vec3(1.0, 0.95, 1.0);
    vec3 starColor2 = vec3(0.9, 0.85, 1.0);

    finalColor += starColor1 * starLayer1;  // Removed multiplier for less intensity
    finalColor += starColor2 * starLayer2 * 0.8;  // Reduced from 1.2 to 0.8

    // Enhance the void effect
    float voidStrength = smoothstep(0.15, 0.0, r);
    finalColor = mix(finalColor, darkVoid, voidStrength);

    gl_FragColor = vec4(finalColor, 1.0);
  }
`;

export const PortalBackground: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const frameRef = useRef<number>();
  const timeRef = useRef<number>(0);
  const lastTimeRef = useRef<number>(0);
  const rendererRef = useRef<THREE.WebGLRenderer | null>(null);

  useEffect(() => {
    if (!containerRef.current) return;

    // Setup
    const scene = new THREE.Scene();
    const camera = new THREE.OrthographicCamera(-1, 1, 1, -1, 0.1, 10);
    camera.position.z = 1;

    // Add mouse tracking
    const lastMousePos = { x: 0, y: 0 };
    const mouseVelocity = { x: 0, y: 0 };
    const handleMouseMove = (e: MouseEvent) => {
      const rect = containerRef.current?.getBoundingClientRect();
      if (!rect) return;

      // Calculate normalized mouse position
      const x = (e.clientX - rect.left) / rect.width;
      const y = 1.0 - (e.clientY - rect.top) / rect.height;

      // Calculate mouse velocity
      mouseVelocity.x = x - lastMousePos.x;
      mouseVelocity.y = y - lastMousePos.y;

      // Update last position
      lastMousePos.x = x;
      lastMousePos.y = y;

      if (material) {
        material.uniforms.mousePos.value.set(x, y);
        material.uniforms.mouseVelocity.value.set(mouseVelocity.x, mouseVelocity.y);
      }
    };

    // Optimize renderer
    const renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      powerPreference: 'high-performance',
      stencil: false,
      depth: false,
      alpha: true,
      precision: 'mediump' // Use medium precision for better performance
    });

    rendererRef.current = renderer;
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));

    // Enable WebGL optimizations
    const gl = renderer.getContext();
    gl.getExtension('OES_standard_derivatives');

    // Disable depth and blend operations for 2D rendering
    gl.disable(gl.DEPTH_TEST);
    gl.disable(gl.BLEND);

    containerRef.current.appendChild(renderer.domElement);

    // Create portal material with optimized uniforms
    const material = new THREE.ShaderMaterial({
      uniforms: {
        time: { value: 0 },
        mousePos: { value: new THREE.Vector2(0.5, 0.5) },
        mouseVelocity: { value: new THREE.Vector2(0.0, 0.0) }
      },
      vertexShader: portalVertexShader,
      fragmentShader: portalFragmentShader,
      transparent: false,
      depthWrite: false,
      depthTest: false
    });

    // Use simpler geometry with minimal segments
    const geometry = new THREE.PlaneGeometry(2, 2, 1, 1);
    const mesh = new THREE.Mesh(geometry, material);
    scene.add(mesh);

    // Optimized animation loop with delta time
    const animate = (timestamp: number) => {
      if (!material || !renderer) return;

      // Calculate smooth delta time
      const deltaTime = timestamp - lastTimeRef.current;
      lastTimeRef.current = timestamp;

      // Update time at a consistent rate, capped at 60fps
      const targetFrameTime = 1000 / 60;
      const frameProgress = Math.min(deltaTime / targetFrameTime, 1);
      timeRef.current += 0.01 * frameProgress; // Consistent time step

      material.uniforms.time.value = timeRef.current;

      renderer.render(scene, camera);
      frameRef.current = requestAnimationFrame(animate);
    };

    // Start animation
    lastTimeRef.current = performance.now();
    frameRef.current = requestAnimationFrame(animate);

    // Optimized resize handler with debounce and size check
    let resizeTimeout: number;
    let lastWidth = window.innerWidth;
    let lastHeight = window.innerHeight;

    const handleResize = () => {
      if (resizeTimeout) {
        cancelAnimationFrame(resizeTimeout);
      }

      resizeTimeout = requestAnimationFrame(() => {
        if (!renderer) return;

        const width = window.innerWidth;
        const height = window.innerHeight;

        // Only resize if dimensions actually changed
        if (width !== lastWidth || height !== lastHeight) {
          renderer.setSize(width, height);
          lastWidth = width;
          lastHeight = height;
        }
      });
    };

    // Add event listeners
    window.addEventListener('resize', handleResize, { passive: true });
    window.addEventListener('mousemove', handleMouseMove, { passive: true });

    // Cleanup
    return () => {
      if (frameRef.current) {
        cancelAnimationFrame(frameRef.current);
      }
      if (resizeTimeout) {
        cancelAnimationFrame(resizeTimeout);
      }
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('mousemove', handleMouseMove);

      renderer.dispose();
      geometry.dispose();
      material.dispose();

      if (containerRef.current?.contains(renderer.domElement)) {
        containerRef.current.removeChild(renderer.domElement);
      }

      rendererRef.current = null;
    };
  }, []);

  return <BackgroundContainer ref={containerRef} />;
};
