import React from 'react';
import styled from 'styled-components';
import { BlotterText } from './BlotterText';

const HeroSection = styled.section`
  min-height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  position: relative;
  background: var(--background);
  padding: 0;
  text-align: center;
  overflow: hidden;
`;

const MainTitle = styled.div`
  width: 100vw;
  height: 100vh;
  margin: 0;
  padding: 0;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
`;

const ContentWrapper = styled.div`
  width: 100%;
  padding: 2rem;
  margin-top: -20vh; // Pull content up to overlap with title
  position: relative;
  z-index: 2;
`;

const SubTitle = styled.div`
  font-size: 1.5rem;
  margin-bottom: 3rem;
  max-width: 800px;
  color: rgba(255, 255, 255, 0.9);
`;

const Headline = styled.div`
  font-size: 2rem;
  margin-bottom: 3rem;
  max-width: 800px;
  color: rgba(255, 255, 255, 0.9);
  font-weight: bold;
`;

const CTAContainer = styled.div`
  display: flex;
  gap: 2rem;
  margin-top: 2rem;
`;

const CTAButton = styled.a`
  padding: 1rem 2rem;
  font-size: 1.2rem;
  border: 2px solid var(--accent);
  border-radius: 8px;
  color: var(--accent);
  text-decoration: none;
  transition: all 0.3s ease;
  background: transparent;
  cursor: pointer;

  &:hover {
    background: var(--accent);
    color: var(--background);
    transform: translateY(-2px);
  }
`;

export const Hero: React.FC = () => {
  return (
    <HeroSection>
      <MainTitle>
        <BlotterText 
          text="SQUANCH" 
          isHeader={true}
          fill="rgba(255, 255, 255, 0.9)"
        />
      </MainTitle>

      <ContentWrapper>
        <SubTitle>
          <BlotterText 
            text="Welcome to the Squanchiverse! Hold on to your tails, you're in for a squanchin' wild ride!"
            fill="rgba(255, 255, 255, 0.7)"
          />
        </SubTitle>

        <Headline>
          <BlotterText 
            text="Enter the Squanchiverse: Where Memes, AI, and Quantum Cats Collide!"
            fill="rgba(255, 255, 255, 0.9)"
          />
        </Headline>

        <CTAContainer>
          <CTAButton href="#meet-ai">Meet Squanchy AI</CTAButton>
          <CTAButton href="#get-token">Get the Token</CTAButton>
        </CTAContainer>
      </ContentWrapper>
    </HeroSection>
  );
};
