import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { config } from '../config';

const noiseAnimation = keyframes`
  0% { transform: translate(0, 0); }
  10% { transform: translate(-5%, -5%); }
  20% { transform: translate(-10%, 5%); }
  30% { transform: translate(5%, -10%); }
  40% { transform: translate(-5%, 15%); }
  50% { transform: translate(-10%, 5%); }
  60% { transform: translate(15%, 0); }
  70% { transform: translate(0, 10%); }
  80% { transform: translate(-15%, 0); }
  90% { transform: translate(10%, 5%); }
  100% { transform: translate(5%, 0); }
`;

const Section = styled.section`
  background-color: black;
  position: relative;
  overflow: hidden;
  min-height: 100vh;
  margin-top: 100vh;
  padding: 0;

  &::before {
    content: '';
    position: absolute;
    top: -150%;
    left: -150%;
    width: 400%;
    height: 400%;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 3000 3000' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.6' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
    opacity: 0.12;
    animation: ${noiseAnimation} 1s steps(2) infinite;
    pointer-events: none;
  }
`;

const FeaturesContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
  padding: 6rem 2rem;
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  position: relative;
  z-index: 1;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    padding: 4rem 1rem;
  }
`;

const FeatureCard = styled.div`
  background: rgba(0, 0, 0, 0.7);
  border: 1px solid rgba(255, 215, 0, 0.3);
  border-radius: 15px;
  padding: 2rem;
  color: white;
  backdrop-filter: blur(10px);
  box-shadow: 0 0 20px rgba(255, 215, 0, 0.1);
  transition: all 0.3s ease;
  min-height: 400px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 0 30px rgba(255, 215, 0, 0.2);
  }
`;

const CardContent = styled.div`
  flex-grow: 1;
`;

const ButtonContainer = styled.div`
  margin-top: 2rem;
`;

const Title = styled.h3`
  font-family: 'Creepster', cursive;
  font-size: 2rem;
  color: gold;
  margin-bottom: 1rem;
  text-align: center;
`;

const Description = styled.p`
  font-size: 1.1rem;
  line-height: 1.6;
  margin-bottom: 2rem;
  color: #ccc;
`;

const Button = styled.button`
  background: linear-gradient(45deg, #4a0080, #8000ff);
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 30px;
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: auto;
  font-family: 'Creepster', cursive;
  text-transform: uppercase;
  letter-spacing: 0.05em;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(128, 0, 255, 0.4);
  }
`;

const ResultContainer = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 10px;
  border: 1px solid rgba(255, 215, 0, 0.2);
`;

const SurvivalGuideHUD = styled.div`
  position: relative;
  padding: 1rem;
  border: 1px solid rgba(128, 0, 255, 0.3);
  border-radius: 10px;
  margin-top: 1rem;

  &::before {
    content: '';
    position: absolute;
    top: -5px;
    left: -5px;
    right: -5px;
    bottom: -5px;
    border: 1px solid rgba(128, 0, 255, 0.1);
    border-radius: 12px;
    pointer-events: none;
  }
`;

const HUDElement = styled.div`
  font-family: monospace;
  font-size: 0.8rem;
  color: rgba(128, 0, 255, 0.8);
  margin: 0.5rem 0;
`;

const StarChart = styled.div`
  position: absolute;
  top: 10px;
  right: 10px;
  width: 100px;
  height: 100px;
  border: 1px solid rgba(128, 0, 255, 0.3);
  border-radius: 50%;
  opacity: 0.5;
`;

interface ChatResponse {
  choices: Array<{
    message: {
      content: string;
    };
  }>;
}

export const InteractiveFeatures: React.FC = () => {
  const [prediction, setPrediction] = useState<string>('');
  const [survivalTip, setSurvivalTip] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);

  const makeOpenAIRequest = async (messages: Array<{ role: string; content: string }>, maxTokens: number) => {
    const apiKey = config.openaiApiKey;

    if (!apiKey) {
      throw new Error('OpenAI API key is not configured');
    }

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: 'gpt-4',
          messages,
          max_tokens: maxTokens
        })
      });

      if (!response.ok) {
        const errorData = await response.text();
        console.error('OpenAI API Error:', {
          status: response.status,
          statusText: response.statusText,
          error: errorData
        });
        throw new Error(`OpenAI API error: ${response.status} ${response.statusText}`);
      }

      const data: ChatResponse = await response.json();
      return data.choices[0]?.message?.content || '';
    } catch (error) {
      console.error('Request error:', error);
      throw error;
    }
  };

  const generatePrediction = async () => {
    setLoading(true);
    try {
      const content = await makeOpenAIRequest([{
        role: "system",
        content: "You are an interdimensional fortune teller who provides quirky, Rick and Morty-style predictions and advice. Keep responses concise and entertaining."
      }, {
        role: "user",
        content: "Generate a random interdimensional prediction or piece of advice."
      }], 100);
      setPrediction(content);
    } catch (error) {
      console.error('Error generating prediction:', error);
      setPrediction('Error connecting to the multiverse. Please try again.');
    }
    setLoading(false);
  };

  const generateSurvivalTip = async () => {
    setLoading(true);
    try {
      const content = await makeOpenAIRequest([{
        role: "system",
        content: "You are an advanced AI survival guide for interdimensional travelers. Provide specific, detailed survival tips for various cosmic scenarios. Include references to specific dimensions, alien species, or cosmic phenomena. Keep responses concise but informative."
      }, {
        role: "user",
        content: "Generate a survival tip for interdimensional travel."
      }], 150);
      setSurvivalTip(content);
    } catch (error) {
      console.error('Error generating survival tip:', error);
      setSurvivalTip('Error accessing survival database. Please try again.');
    }
    setLoading(false);
  };

  const getCurrentCosmicTime = () => {
    const date = new Date();
    return `${date.getUTCHours()}:${date.getUTCMinutes()} UTC`;
  };

  return (
    <Section>
      <FeaturesContainer>
        <FeatureCard>
          <CardContent>
            <Title>Quantum Randomizer</Title>
            <Description>
              Dive into the multiverse for your personalized prediction or Squanchy-certified advice.
            </Description>
            {prediction && (
              <ResultContainer>
                {prediction}
              </ResultContainer>
            )}
          </CardContent>
          <ButtonContainer>
            <Button onClick={generatePrediction} disabled={loading}>
              {loading ? 'Accessing Multiverse...' : 'Generate Your Squanch!'}
            </Button>
          </ButtonContainer>
        </FeatureCard>

        <FeatureCard>
          <CardContent>
            <Title>Surviving the Journey</Title>
            <Description>
              Essential survival tips for interdimensional travelers.
            </Description>
            <StarChart />
            {survivalTip && (
              <SurvivalGuideHUD>
                <HUDElement>⌘ Cosmic Time: {getCurrentCosmicTime()}</HUDElement>
                <HUDElement>☆ Dimensional Coordinates: C-137</HUDElement>
                <HUDElement>⚠ Survival Protocol:</HUDElement>
                {survivalTip}
              </SurvivalGuideHUD>
            )}
          </CardContent>
          <ButtonContainer>
            <Button onClick={generateSurvivalTip} disabled={loading}>
              {loading ? 'Scanning Dimensions...' : 'Get Survival Tip'}
            </Button>
          </ButtonContainer>
        </FeatureCard>
      </FeaturesContainer>
    </Section>
  );
};
