import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { config } from '../config';

const noiseAnimation = keyframes`
  0% { transform: translate(0, 0); }
  10% { transform: translate(-5%, -5%); }
  20% { transform: translate(-10%, 5%); }
  30% { transform: translate(5%, -10%); }
  40% { transform: translate(-5%, 15%); }
  50% { transform: translate(-10%, 5%); }
  60% { transform: translate(15%, 0); }
  70% { transform: translate(0, 10%); }
  80% { transform: translate(-15%, 0); }
  90% { transform: translate(10%, 5%); }
  100% { transform: translate(5%, 0); }
`;

const Section = styled.section`
  background-color: black;
  position: relative;
  overflow: hidden;
  min-height: 100vh;
  margin-top: 0;
  padding: 0;

  &::before {
    content: '';
    position: absolute;
    top: -150%;
    left: -150%;
    width: 400%;
    height: 400%;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 3000 3000' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.6' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
    opacity: 0.12;
    animation: ${noiseAnimation} 1s steps(2) infinite;
    pointer-events: none;
  }
`;

const FeaturesContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: stretch;
  width: calc(100% - 40px);
  height: calc(100vh - 40px);
  margin: 20px;
  position: relative;
  z-index: 1;
  gap: 20px;
`;

const TerminalSection = styled.div`
  flex: 2;
  background: rgba(0, 0, 0, 0.7);
  border-right: 1px solid rgba(255, 215, 0, 0.3);
  display: flex;
  flex-direction: column;
  position: relative;
  overflow: hidden;
`;

const StaticSection = styled.div`
  flex: 1;
  background: #000;
  position: relative;
  overflow: hidden;
  height: 100%;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      repeating-radial-gradient(
        circle at 50% 50%,
        rgba(0, 0, 0, 0.2),
        rgba(0, 0, 0, 0.3) 2px,
        rgba(0, 0, 0, 0.4) 3px
      ),
      linear-gradient(
        to bottom,
        rgba(255, 255, 255, 0.1),
        rgba(255, 255, 255, 0.05)
      );
    opacity: 0.3;
    animation: ${noiseAnimation} 0.5s steps(2) infinite;
  }
`;

const QuantumTerminal = styled.div`
  flex: 1;
  background: transparent;
  display: flex;
  flex-direction: column;
  position: relative;
  height: 100%;
  overflow: hidden;
`;

const glowPulse = keyframes`
  0% { box-shadow: 0 0 20px rgba(128, 0, 255, 0.4); }
  50% { box-shadow: 0 0 40px rgba(128, 0, 255, 0.6); }
  100% { box-shadow: 0 0 20px rgba(128, 0, 255, 0.4); }
`;

const MainButton = styled.button`
  background: black;
  color: white;
  border: none;
  padding: 1.5rem 3rem;
  border-radius: 30px;
  font-size: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
  font-family: 'Creepster', cursive;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  animation: ${glowPulse} 2s infinite;

  &:hover {
    transform: scale(1.05);
    animation: ${glowPulse} 1s infinite;
  }
`;

const TerminalColors = {
  success: '#98c379', // green
  error: '#e06c75',   // red
  warning: '#e5c07b', // yellow
  info: '#61afef',    // blue
  prompt: '#c678dd',  // purple
  path: '#56b6c2',    // cyan
  command: '#abb2bf', // white
  comment: '#5c6370'  // gray
};

const LoadingSequence = styled.div`
  font-family: 'Source Code Pro', monospace;
  font-size: 1rem;
  color: ${TerminalColors.command};
  text-align: left;
  width: 100%;
  height: 100%;
  background: #000000;
  padding: 1rem;
  border: none;
  overflow-y: auto;
  position: relative;
  scroll-behavior: smooth;

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.3);
  }

  &::-webkit-scrollbar-thumb {
    background: ${TerminalColors.comment};
    border-radius: 4px;
  }
`;

const ProgressText = styled.div`
  margin: 0.2rem 0;
  opacity: 0.9;
  position: relative;
  padding-left: 1.5rem;
  line-height: 1.4;
  color: ${TerminalColors.command};

  &::before {
    content: '➜';
    position: absolute;
    left: 0;
    color: ${TerminalColors.prompt};
  }

  span {
    color: inherit;
  }
`;

const Spinner = styled.div`
  display: inline-block;
  margin-right: 8px;
  width: 1em;
  height: 1em;
  position: relative;

  &::after {
    content: '⠋';
    animation: spinnerAnimation 1s steps(10, end) infinite;
  }

  @keyframes spinnerAnimation {
    11% { content: '⠙'; }
    22% { content: '⠹'; }
    33% { content: '⠸'; }
    44% { content: '⠼'; }
    55% { content: '⠴'; }
    66% { content: '⠦'; }
    77% { content: '⠧'; }
    88% { content: '⠇'; }
    99% { content: '⠏'; }
  }
`;

const LoadingBar = styled.div`
  font-family: 'Source Code Pro', monospace;
  color: ${TerminalColors.command};
  margin: 0.2rem 0;
  opacity: 0.9;
  white-space: pre;
`;

const ErrorText = styled.div`
  color: ${TerminalColors.error};
  margin: 0.2rem 0;
  padding-left: 1.5rem;
  opacity: 0.9;
`;

const WarningText = styled.div`
  color: ${TerminalColors.warning};
  margin: 0.2rem 0;
  padding-left: 1.5rem;
  opacity: 0.9;
`;

const RetryText = styled.div`
  color: ${TerminalColors.info};
  margin: 0.2rem 0;
  padding-left: 1.5rem;
  opacity: 0.9;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 8px;
  height: 15px;
  background: #0f0;
  margin-left: 4px;
  animation: blink 1s step-end infinite;

  @keyframes blink {
    50% { opacity: 0; }
  }
`;

const ArtifactDisplay = styled.div`
  height: 100%;
  padding: 20px;
  background: #000000;
  font-family: 'Source Code Pro', monospace;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  overflow-y: auto;
  text-align: left;

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.3);
  }

  &::-webkit-scrollbar-thumb {
    background: ${TerminalColors.comment};
    border-radius: 4px;
  }
`;

const ArtifactImage = styled.img`
  width: 100%;
  max-width: 400px;
  height: auto;
  margin: 2rem auto;
  display: block;
  border-radius: 10px;
  border: 2px solid rgba(128, 0, 255, 0.3);
`;

const ArtifactText = styled.div`
  font-family: 'Source Code Pro', monospace;
  font-size: 1rem;
